import Element from "./Element"
import axios from "axios"
import { useState, useEffect } from 'react';


export default function Table({}){

//https://api.coincap.io/v2/assets/bitcoin
//https://api.coincap.io/v2/assets
//https://assets.coincap.io/assets/icons/${symbol}@2x.png

    const url = 'https://api.coincap.io/v2/assets'

    const [listCryptos, setListCryptos] = useState([])

    useEffect(()=>{
      axios.get(url).then((resp)=>{
        setListCryptos(resp.data.data)
      })
    }, [])

    return(
        <table>
            <tbody>
                <tr >
                    <th>Rank</th>
                    <th>Icon</th>
                    <th>Crypto</th>
                    <th>Price</th>
                    <th>24h Change</th>
                </tr>
                {listCryptos.map((el,i)=> {

                    return(
                        <Element key={i} rank={el.rank} 
                        icon={'X'}
                        name={el.name} 
                        symbol={el.symbol} 
                        priceUsd={el.priceUsd}  
                        percent24={el.changePercent24Hr}/>   
                    )
                })}
            </tbody>
        </table>
    )
}
