import axios from "axios"
import { useEffect, useState } from "react"
import { useParams, Link } from "react-router-dom"


export default function MainInfo(){
    let{name} = useParams()
    const [data, setData] = useState()

    useEffect(() => {
        axios.get(`https://api.coincap.io/v2/assets/${name}`).then((resp)=> {
             setData(resp.data.data)
        })
    }, [name])

    return(
        <div>
            <Link to='/'>&#8592; Back</Link>
            <div className="grid_container">  
                <h1 className="grid_item">X</h1>
                <p className="grid_item">{data?.rank}</p>
                <p className="grid_item">{data?.symbol}</p>
                <p className="grid_item">{data?.name}</p>
                <p className="grid_item">{data?.supply}</p>
                <p className="grid_item">{data?.maxSupply}</p>
                <p className="grid_item">{data?.marketCapUsd}</p>
                <p className="grid_item">{data?.volumeUsd24Hr}</p>
                <p className="grid_item">{data?.priceUsd}</p>
                <p className="grid_item">{data?.changePercent24Hr}</p>
                <p className="grid_item">{data?.vwap24Hr}</p>
                <p className="grid_item">{data?.explorer}</p>
            </div>

        </div>

    )
}
